 /* jshint bitwise : true, eqeqeq : true, forin : true, noarg : true, noempty : true, nonew : true, laxbreak : true, curly: false, mocha: true,
   asi : true, esnext : true, laxcomma : true, sub : true, browser : true, node : true, phantom : true, strict: false, eqnull: true */

const
  gulp = require('gulp')
, jshint = require('gulp-jshint')
, stylish = require('jshint-stylish')
, mocha = require('gulp-mocha')
, scripts = ['./*.js']
, tests = ['./test/test.js']

gulp.task('lint', function() {
  return gulp.src(scripts)
    .pipe(jshint())
    .pipe(jshint.reporter(stylish, { verbose : true }))
})

gulp.task('mocha', function() {
  return gulp.src('./test/test.js', {read : false})
    .pipe(mocha({ reporter : 'spec', ui : 'bdd' }))
})

gulp.task('watch', function() {
  gulp.watch(scripts, ['lint'])
})

gulp.task('default', ['lint', 'mocha'])