 /* jshint bitwise : true, eqeqeq : true, forin : true, noarg : true, noempty : true, nonew : true, laxbreak : true, curly: false, mocha: true,
   asi : true, esnext : true, laxcomma : true, sub : true, browser : true, node : true, phantom : true, strict: false, eqnull: true */

module.exports = function falsify() {
  var pseudoFalses = Array.prototype.slice.call(arguments)

  return function(valueToCheck) {
    var didMatch = pseudoFalses.some(
      function(pseudoFalse) {
        return valueToCheck === pseudoFalse
      }
    )

    if (didMatch) return false
    else return !!valueToCheck
  }
}